import { useEffect, useState } from "react";
import { CONTRACTS_BY_CHAIN } from "./constants/contracts";
import {
  BrowserProvider,
  Contract,
  formatUnits
} from "ethers";
import TokenCard from "./components/TokenCard";

export default function App() {
  const [signer, setSigner] = useState(null);
  const [config, setConfig] = useState(null);

  useEffect(() => {
    const setup = async () => {
      if (!window.ethereum) {
        alert("No wallet found ‚Äî please install MetaMask or compatible extension.");
        return;
      }

      const provider = new BrowserProvider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signerInstance = await provider.getSigner();
      setSigner(signerInstance);

      const { chainId } = await provider.getNetwork();
      console.log("üîç Detected Chain ID:", chainId);

      const cfg = CONTRACTS_BY_CHAIN[chainId];
      if (!cfg) {
        alert(`Unsupported chain ID: ${chainId}`);
        return;
      }

      setConfig(cfg);
    };

    setup();
  }, []);

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>ü™ô JUSD Wallet Sam Technology</h1>
      {!config ? (
        <p>‚è≥ Detecting chain...</p>
      ) : (
        <>
          <p>üåê Connected to: <strong>{config.name}</strong></p>
          {Object.entries(config.tokens).map(([name, addr]) => (
            <TokenCard
              key={name}
              name={name}
              address={addr}
              signer={signer}
            />
          ))}
        </>
      )}
    </div>
  );
}
