import {
  Contract,
  formatUnits,
  parseUnits
} from "ethers";

const TOKEN_METADATA = {
  JUSDC: { chain: "Ethereum", mintable: true, uniswap: true },
  wUSDC: { chain: "Polygon", mintable: false, uniswap: true },
  JUSDT: { chain: "Base", mintable: true, uniswap: false },
  wUSDT: { chain: "Polygon", mintable: false, uniswap: false },
};

export default function TokenCard({ name, address, signer }) {
  const fetchBalance = async () => {
    try {
      const contract = new Contract(address, [
        "function balanceOf(address) view returns (uint256)"
      ], signer);

      const user = await signer.getAddress();
      const bal = await contract.balanceOf(user);

      alert(`${name} balance: ${formatUnits(bal, 6)}`);
    } catch (err) {
      console.error("Balance error:", err);
      alert("Failed to fetch balance");
    }
  };

  const importToken = async () => {
    try {
      await window.ethereum.request({
        method: "wallet_watchAsset",
        params: {
          type: "ERC20",
          options: {
            address,
            symbol: name,
            decimals: 6,
            image: `${window.location.origin}/assets/${name}.png`
          }
        }
      });
    } catch (err) {
      console.error("Token import error:", err);
      alert("Failed to import token");
    }
  };

  const mintToken = async () => {
    try {
      const contract = new Contract(address, [
        "function mint(address to, uint256 amount)"
      ], signer);

      const user = await signer.getAddress();
      const amount = parseUnits("100", 6);
      const tx = await contract.mint(user, amount);
      await tx.wait();

      alert(`ðŸ§ª Minted 100 ${name} successfully`);
    } catch (err) {
      console.error("Mint error:", err);
      alert("Mint failed");
    }
  };

  const metadata = TOKEN_METADATA[name] || {};

  return (
    <div style={{
      border: "1px solid #ccc",
      padding: "1rem",
      marginBottom: "1rem",
      borderRadius: "8px",
      boxShadow: "0 2px 5px rgba(0,0,0,0.1)"
    }}>
      <h3 style={{ display: "flex", alignItems: "center" }}>
        <img
          src={`/assets/${name}.png`}
          alt={`${name} logo`}
          style={{
            width: "36px",
            height: "36px",
            marginRight: "0.75rem",
            borderRadius: "6px"
          }}
        />
        {name}
      </h3>
      <p><strong>Contract:</strong> {address}</p>
      <p><strong>Chain:</strong> {metadata.chain || "Unknown"}</p>

      <div style={{ display: "flex", gap: "0.5rem", flexWrap: "wrap", marginTop: "0.5rem" }}>
        <button onClick={fetchBalance}>ðŸ’° View Balance</button>
        <button onClick={importToken}>âž• Add to Wallet</button>
        {metadata.mintable && (
          <button onClick={mintToken}>ðŸ§ª Mint 100 {name}</button>
        )}
        {metadata.uniswap && (
          <a
            href={`https://app.uniswap.org/#/swap?outputCurrency=${address}`}
            target="_blank"
            rel="noopener noreferrer"
            style={{
              padding: "0.5rem 1rem",
              border: "1px solid #bbb",
              borderRadius: "4px",
              textDecoration: "none",
              background: "#f6f6f6",
              color: "#444"
            }}
          >
            ðŸŸª View on Uniswap
          </a>
        )}
      </div>
    </div>
  );
}
